        private readonly AppDbContext _appDbContext;
        private readonly IEmailSenderService _emailSenderService;

        public OrderController(AppDbContext appDbContext, IEmailSenderService emailSenderService)
        {
            _appDbContext = appDbContext;
            _emailSenderService = emailSenderService;
        }
        public IActionResult Index(int page = 1)
        {
            var query = _appDbContext.Orders.Include(x => x.OrderItem).Where(x => x.IsCancel == false).AsQueryable();
            PaginatedList<Order> orders = PaginatedList<Order>.Create(query, 5, page);
            return View(orders);
        }
        public IActionResult CancelIndex(int page = 1)
        {
            var query = _appDbContext.Orders.Where(x => x.IsCancel == true).Include(x => x.OrderItem).AsQueryable();
            PaginatedList<Order> orders = PaginatedList<Order>.Create(query, 5, page);
            return View(orders);
        }
        public IActionResult Detail(Guid id)
        {
            Order order = _appDbContext.Orders.Include(x => x.OrderItem).FirstOrDefault(x => x.Id == id);
            if (order == null) return View("Error");
            return View(order);
        }
